/**
 * Created by hao.cheng on 2017/4/13.
 */
import React, {Component} from 'react';
import {Card, Form, Input, Tooltip, Icon, Cascader, Select, Row, Col, Checkbox, Button} from 'antd';
import LoginForm from '../forms/LoginForm';
import ModalForm from '../forms/ModalForm';
import HorizontalForm from '../forms/HorizontalForm';
import BreadcrumbCustom from '../BreadcrumbCustom';
import NewSelectTable from './NewSelectTable'
import axios from 'axios'
const FormItem = Form.Item;
const Option = Select.Option;

const url = 'http://localhost:5000/v1'
class BasicForms extends Component {
    state = {
        confirmDirty: false,
        residences: [],
        strategy: [{
            value: '类别内销售最好',
            label: '类别内销售最好',

        }, {
            value: '小类中销售最好',
            label: '小类中销售最好',
        }],
        new_rule:[
            {
                value: '最高毛利率',
                label: '最高毛利率',

            }, {
                value: '店均销量',
                label: '店均销量',
            }
        ],
        data: []

    };



    child_data(data) {
        console.log('这是一个data')
        this.setState({
            parentText: data //把父组件中的parentText替换为子组件传递的值
        },() =>{
            console.log(this.state.parentText);//setState是异步操作，但是我们可以在它的回调函数里面进行操作
        });

    }

    componentDidMount() {
        const _this = this;
        axios.get(url + '/cm_categories')
            .then(function (response) {
                console.log('yangshuyu');
                console.log(response.data.data);

                var options = []

                response.data.data.map(item => {
                    console.log(item);
                    options.push({
                        value: item.category,
                        label: item.category,
                    })
                })


                _this.setState({
                    residences: options,
                    isLoaded: true
                });
            })
            .catch(function (error) {
                console.log(error);
                _this.setState({
                    isLoaded: false,
                    error: error
                })
            })

    }

    handleSubmit = () => {
        // e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
            console.log('yangshuyu')
            console.log(this)
            var category = values.category[0]
            var new_amount = values.new_amount
            var start_price = values.start_price
            var end_price = values.end_price
            var new_rule = values.new_rule
            var strategy = values.strategy[0]

            if (strategy == '类别内销售最好') {
                strategy = 0
            } else {
                strategy = 1
            }
            if (new_rule == '最高毛利率') {
                new_rule = 0
            } else {
                new_rule = 1
            }

            axios.get('http://localhost:5000/v1/new_goods',{
                'params': {
                    'q': category,
                    'new_amount': new_amount,
                    'start_price': start_price,
                    'end_price': end_price,
                    'strategy': strategy,
                    'new_rule':new_rule
                }

            })
                .then((response) =>{
                    console.log(response.data)
                    this.setState({
                        data: response.data.data,
                        isLoaded: true
                    });
                })

                .catch((response)=> {
                    console.log(response);
                    this.setState({
                        isLoaded: false,
                        error: response
                    })
                })

        });
    };

    onChange = (value, selectedOptions) => {
        console.log(value);
        console.log(selectedOptions[3])
        this.setState({
            inputValue: selectedOptions.map(o=>o.label).join(', ')
        });
        const onChange = this.props.onChange;

        if (onChange) {
            onChange({...value});
        }
    }

    render() {
        const {getFieldDecorator} = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: {span: 24},
                sm: {span: 8},
            },
            wrapperCol: {
                xs: {span: 24},
                sm: {span: 14},
            },
        };

        return (
            <div className="gutter-example">
                <BreadcrumbCustom first="汰换" second="换新"/>
                <Form layout="inline" onSubmit={()=>this.handleSubmit()}>
                    <div className="gutter-box">
                        <Card title="水平表单" bordered={false}>
                            <FormItem
                                {...formItemLayout}
                                label="选新策略"
                            >
                                {getFieldDecorator('strategy', {
                                    initialValue: ['类别内销售最好'],
                                    rules: [{type: 'array', required: true, message: '请选择选新策略!'}],
                                })(
                                    <Cascader options={this.state.strategy}/>
                                )}

                            </FormItem>

                            <FormItem
                                {...formItemLayout}
                                label="核心指标"
                            >
                                {getFieldDecorator('new_rule', {
                                    initialValue: ['最高毛利率'],
                                    rules: [{type: 'array', required: true, message: '请选择核心指标!'}],
                                })(
                                    <Cascader options={this.state.new_rule} onChange={this.onChange}/>
                                )}

                            </FormItem>


                            <FormItem
                                {...formItemLayout}
                                label="商品分类"
                            >
                                {getFieldDecorator('category', {
                                    initialValue: ['zhejiang'],
                                    rules: [{type: 'array', required: true, message: '商品分类!'}],
                                })(
                                    <Cascader options={this.state.residences} onChange={this.onChange}/>
                                )}

                            </FormItem>

                            <FormItem>
                                <div>零售价:</div>
                            </FormItem>

                            <FormItem>
                                {getFieldDecorator('start_price', {
                                    initialValue: ' ',
                                    rules: [{ required: true, message: '最低零售价' }],
                                })(
                                    <Input style={{ width: 80 }} Inputplaceholder="最低零售价" width="30"/>
                                )}
                            </FormItem>
                            <FormItem>
                                <div>至</div>
                            </FormItem>
                            <FormItem>
                                {getFieldDecorator('end_price', {
                                    initialValue: ' ',
                                    rules: [{ required: true, message: '最高零售价' }],
                                })(
                                    <Input style={{ width: 80 }} placeholder="最高零售价" />
                                )}
                            </FormItem>


                            <FormItem>
                                <div>新品个数:</div>
                            </FormItem>

                            <FormItem>
                                {getFieldDecorator('new_amount', {
                                    initialValue: 3,
                                    rules: [{ required: true, message: '新品个数' }],
                                })(
                                    <Input style={{ width: 130 }} placeholder="新品个数" />
                                )}
                            </FormItem>

                        </Card>

                        <Card bordered={false}>
                            <FormItem>
                                <Button
                                    type="primary"
                                    htmlType="submit"
                                >

                                    计算
                                </Button>
                            </FormItem>
                        </Card>



                    </div>
                </Form>
                <Card  bordered={false}>
                    <NewSelectTable data={this.state.data} pfn={this.child_data.bind(this)}/>
                </Card>

            </div>
        )
    }
}

const Gallery = Form.create()(BasicForms);

export default Gallery;
