/**
 * Created by hao.cheng on 2017/4/13.
 */
import React, {Component} from 'react';
import {Card, Form, Input, Tooltip, Icon, Cascader, Select, Row, Col, Checkbox, Button} from 'antd';
import LoginForm from '../forms/LoginForm';
import ModalForm from '../forms/ModalForm';
import HorizontalForm from '../forms/HorizontalForm';
import BreadcrumbCustom from '../BreadcrumbCustom';
import axios from 'axios'
const FormItem = Form.Item;
const Option = Select.Option;

const resi = [{
    value: 'zhejiang',
    label: 'Zhejiang',
    children: [{
        value: 'hangzhou',
        label: 'Hangzhou',
        children: [{
            value: 'xihu',
            label: 'West Lake',
        }],
    }],
}, {
    value: 'jiangsu',
    label: 'Jiangsu',
    children: [{
        value: 'nanjing',
        label: 'Nanjing',
        children: [{
            value: 'zhonghuamen',
            label: 'Zhong Hua Men',
        }],
    }],
}];


class BasicForms extends Component {
    state = {
        confirmDirty: false,
        residences: [],
        strategy: [{
            value: 'zhejiang',
            label: 'Zhejiang',
            children: [{
                value: 'hangzhou',
                label: 'Hangzhou',
                children: [{
                    value: 'xihu',
                    label: 'West Lake',
                }],
            }],
        }, {
            value: 'jiangsu',
            label: 'Jiangsu',
            children: [{
                value: 'nanjing',
                label: 'Nanjing',
                children: [{
                    value: 'zhonghuamen',
                    label: 'Zhong Hua Men',
                }],
            }],
        }]

    };

    componentDidMount() {
        const _this = this;
        axios.get('http://localhost:5000/v1/categories')
            .then(function (response) {
                console.log('yangshuyu');
                console.log(response.data.data);

                var options = []

                response.data.data.map(item => {
                    console.log(item);
                    options.push({
                        value: item.id,
                        label: item.name,
                        children: item.foreign_category_lv2s.map(lv2 => {
                            return {
                                name: lv2.id,
                                label: lv2.name,
                                children: lv2.foreign_category_lv3s.map(lv3 => {
                                    return {
                                        name: lv3.id,
                                        label: lv3.name,
                                        children: lv3.foreign_category_lv4s.map(lv4 => {
                                            return {
                                                name: lv4.id,
                                                label: lv4.name,
                                            }
                                        })
                                    }
                                })
                            }
                        })
                    })

                })


                _this.setState({
                    residences: options,
                    isLoaded: true
                });
            })
            .catch(function (error) {
                console.log(error);
                _this.setState({
                    isLoaded: false,
                    error: error
                })
            })

    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
        });
    };

    onChange = (value, selectedOptions) => {
        console.log(value);
        console.log(selectedOptions[3])
        this.setState({
            inputValue: selectedOptions.map(o=>o.label).join(', ')
        });
        const onChange = this.props.onChange;

        if (onChange) {
            onChange({...value});
        }
    }

    render() {
        const {getFieldDecorator} = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: {span: 24},
                sm: {span: 8},
            },
            wrapperCol: {
                xs: {span: 24},
                sm: {span: 14},
            },
        };
        const tailFormItemLayout = {
            wrapperCol: {
                xs: {
                    span: 24,
                    offset: 0,
                },
                sm: {
                    span: 14,
                    offset: 8,
                },
            },
        };
        const prefixSelector = getFieldDecorator('prefix', {
            initialValue: '86',
        })(
            <Select className="icp-selector" style={{width: '60px'}}>
                <Option value="86">+86</Option>
            </Select>
        );
        return (
            <div className="gutter-example">
                <BreadcrumbCustom first="汰换" second="淘汰"/>

                <Form layout="inline" onSubmit={this.handleSubmit}>
                    <div className="gutter-box">
                        <Card title="水平表单" bordered={false}>

                            <FormItem
                                {...formItemLayout}
                                label="常住地址"
                            >
                                {getFieldDecorator('residence', {
                                    initialValue: ['zhejiang', 'hangzhou', 'xihu'],
                                    rules: [{type: 'array', required: true, message: '请选择你的常住地址!'}],
                                })(
                                    <Cascader options={this.state.strategy}/>
                                )}
                            </FormItem>

                            <FormItem
                                {...formItemLayout}
                                label="淘汰策略"
                            >
                                {getFieldDecorator('strategy', {
                                    initialValue: ['zhejiang'],
                                    rules: [{type: 'array', required: true, message: '请选择淘汰策略!'}],
                                })(
                                    <Cascader options={this.state.resi1}/>
                                )}

                            </FormItem>

                            <FormItem
                                {...formItemLayout}
                                label="常住地址"
                            >
                                {getFieldDecorator('residence', {
                                    initialValue: ['zhejiang'],
                                    rules: [{type: 'array', required: true, message: '请选择你的常住地址!'}],
                                })(
                                    <Cascader options={this.state.residences} onChange={this.onChange}/>
                                )}

                            </FormItem>

                        </Card>
                    </div>
                </Form>

            </div>
        )
    }
}

const MapUi = Form.create()(BasicForms);

export default MapUi;
