/**
 * Created by hao.cheng on 2017/4/13.
 */
import React, {Component} from 'react';
import {Card, Form, Input, Tooltip, Icon, Cascader, Select, Row, Col, Checkbox, Button} from 'antd';
import LoginForm from '../forms/LoginForm';
import ModalForm from '../forms/ModalForm';
import HorizontalForm from '../forms/HorizontalForm';
import BreadcrumbCustom from '../BreadcrumbCustom';
import SelectTable from '../tables/SelectTable'
import axios from 'axios'
const FormItem = Form.Item;
const Option = Select.Option;

class BasicForms extends Component {
    state = {
        confirmDirty: false,
        residences: [],
        strategy: [{
            value: '类别内销售最差',
            label: '类别内销售最差',

        }, {
            value: '小类中销售最差',
            label: '小类中销售最差',
        }],
        indicator:[
            {
                value: '店均销售量',
                label: '店均销售量',

            }, {
                value: '毛利率',
                label: '毛利率',
            }
        ],
        data: [],
        chain_category: '',
        area:''

    };



    child_data(data) {
        console.log('这是一个data')
        this.setState({
            parentText: data //把父组件中的parentText替换为子组件传递的值
        },() =>{
            console.log(this.state.parentText);//setState是异步操作，但是我们可以在它的回调函数里面进行操作
        });

    }

    componentDidMount() {
        const _this = this;
        axios.get('http://localhost:5000/v1/categories')
            .then(function (response) {
                console.log('yangshuyu');
                console.log(response.data.data);

                var options = []

                response.data.data.map(item => {
                    console.log(item);
                    options.push({
                        value: item.id,
                        label: item.name,
                        children: item.foreign_category_lv2s.map(lv2 => {
                            return {
                                name: lv2.id,
                                label: lv2.name,
                                children: lv2.foreign_category_lv3s.map(lv3 => {
                                    return {
                                        name: lv3.id,
                                        label: lv3.name,
                                        children: lv3.foreign_category_lv4s.map(lv4 => {
                                            return {
                                                name: lv4.id,
                                                label: lv4.name,
                                            }
                                        })
                                    }
                                })
                            }
                        })
                    })

                })


                _this.setState({
                    residences: options,
                    isLoaded: true
                });
            })
            .catch(function (error) {
                console.log(error);
                _this.setState({
                    isLoaded: false,
                    error: error
                })
            })

    }

    handleSubmit = () => {
        // e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
            console.log(this.state.chain_category)
            console.log(this.state.storage_area)


            axios.get('http://localhost:5000/v1/eliminate_goods?chain_category=71753178')
                .then((response) =>{
                    console.log(response.data.data)
                    this.setState({
                        data: response.data.data,
                        isLoaded: true
                    });
                })
                .catch((response)=> {
                    console.log(response);
                    this.setState({
                        isLoaded: false,
                        error: response
                    })
                })

        });
    };

    onCategoryChange = (value, selectedOptions) => {
        console.log(value);
        console.log(selectedOptions[3])
        this.setState({
            chain_category: selectedOptions.map(o=>o.label).join(', ')
        });
        const onChange = this.props.onChange;

        if (onChange) {
            onChange({...value});
        }
    }

    onAreaChange = (value, selectedOptions) => {
        console.log(value);
        console.log(selectedOptions)
        this.setState({
            area: selectedOptions
        });
        const onChange = this.props.onChange;

        if (onChange) {
            onChange({...value});
        }
    }


    render() {
        const {getFieldDecorator} = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: {span: 24},
                sm: {span: 8},
            },
            wrapperCol: {
                xs: {span: 24},
                sm: {span: 14},
            },
        };

        return (
            <div className="gutter-example">
                <BreadcrumbCustom first="汰换" second="淘汰"/>

                <Form layout="inline" onSubmit={()=>this.handleSubmit()}>
                    <div className="gutter-box">
                        <Card title="水平表单" bordered={false}>

                            <FormItem
                                {...formItemLayout}
                                label="淘汰策略"
                            >
                                {getFieldDecorator('strategy', {
                                    initialValue: ['类别内销售最差'],
                                    rules: [{type: 'array', required: true, message: '请选择淘汰策略!'}],
                                })(
                                    <Cascader options={this.state.strategy}/>
                                )}

                            </FormItem>

                            <FormItem
                                {...formItemLayout}
                                label="核心指标"
                            >
                                {getFieldDecorator('indicator', {
                                    initialValue: ['店均销售量'],
                                    rules: [{type: 'array', required: true, message: '请选择核心指标!'}],
                                })(
                                    <Cascader options={this.state.indicator} />
                                )}

                            </FormItem>

                            <FormItem
                                {...formItemLayout}
                                label="店内分区"
                            >
                                {getFieldDecorator('storage_area', {
                                    initialValue: ['zhejiang'],
                                    rules: [{type: 'array', required: false, message: '请选择店内分区!'}],
                                })(
                                    <Cascader options={this.state.residences} onChange={()=>this.onAreaChange()}/>
                                )}

                            </FormItem>


                            <FormItem
                                {...formItemLayout}
                                label="商品分类"
                            >
                                {getFieldDecorator('category', {
                                    initialValue: ['zhejiang'],
                                    rules: [{type: 'array', required: true, message: '商品分类!'}],
                                })(
                                    <Cascader options={this.state.residences} onChange={()=>this.onCategoryChange()}/>
                                )}

                            </FormItem>

                            <FormItem>
                                <div>汰品个数</div>
                            </FormItem>

                            <FormItem>
                                {getFieldDecorator('password', {
                                    rules: [{ required: true, message: '汰品个数' }],
                                })(
                                    <Input placeholder="汰品个数" />
                                )}
                            </FormItem>

                        </Card>

                        <Card bordered={false}>
                            <FormItem>
                                <Button
                                    type="primary"
                                    htmlType="submit"
                                >

                                    计算
                                </Button>
                            </FormItem>
                        </Card>

                    </div>
                </Form>
                <Card title="基础表格" bordered={false}>
                    <SelectTable data={this.state.data} pfn={this.child_data.bind(this)}/>
                </Card>

            </div>
        )
    }
}

const MapUi = Form.create()(BasicForms);

export default MapUi;
