/**
 * Created by hao.cheng on 2017/4/13.
 */
import React, {Component} from 'react';
import {Card, Form, Input, Tooltip, Icon, Cascader, Select, Row, Col, Checkbox, Button} from 'antd';
import LoginForm from '../forms/LoginForm';
import ModalForm from '../forms/ModalForm';
import HorizontalForm from '../forms/HorizontalForm';
import BreadcrumbCustom from '../BreadcrumbCustom';
import axios from 'axios'
const FormItem = Form.Item;
const Option = Select.Option;


class BasicForms extends Component {
    state = {
        confirmDirty: false,
        residences: [{
            value: 'zhejiang',
            label: 'Zhejiang',
            children: [{
                value: 'hangzhou',
                label: 'Hangzhou',
                children: [{
                    value: 'xihu',
                    label: 'West Lake',
                }],
            }],
        }, {
            value: 'jiangsu',
            label: 'Jiangsu',
            children: [{
                value: 'nanjing',
                label: 'Nanjing',
                children: [{
                    value: 'zhonghuamen',
                    label: 'Zhong Hua Men',
                }],
            }],
        }],
        user:[]

    };

    componentDidMount() {
        const _this = this;
        axios.get('http://localhost:5000/v1/categories')
            .then(function (response) {
                console.log('yangshuyu');
                console.log(response.data.data);

                var json1 = response.data.data, options = []
                for(var val in json1){
                    var json2 = json1[val],option1 = []
                    for(var val2 in json2){
                        var json3 = json2[val2],option2 = []
                        for(var val3 in json3){
                            let obj = json3[val3]
                            if(/^\d+$/.test(val3)) option2.push({'value':obj,'label':obj})
                        }
                        option1.push({'value':val2,'label':val2,'children':option2})
                    }
                    // options.push({'value':val,'label':val,'children':option1})
                }

                response.data.data.map(item => {
                    console.log(item);
                    options.push()
                    return {
                        value:item.name,
                        label:item.id,
                        children: item.foreign_category_lv2s.map(city => {
                            return {
                                name:city.name,
                                label:item.id,
                                children:city.foreign_category_lv3s.map(area => {
                                    return {
                                        name: area,
                                        label:area
                                    }
                                })
                            }
                        })
                    }
                })


                _this.setState({
                    residences: options,
                    isLoaded: true
                });
            })
            .catch(function (error) {
                console.log(error);
                _this.setState({
                    isLoaded: false,
                    error: error
                })
            })

    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
        });
    };

    render() {
        const {getFieldDecorator} = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: {span: 24},
                sm: {span: 8},
            },
            wrapperCol: {
                xs: {span: 24},
                sm: {span: 14},
            },
        };
        const tailFormItemLayout = {
            wrapperCol: {
                xs: {
                    span: 24,
                    offset: 0,
                },
                sm: {
                    span: 14,
                    offset: 8,
                },
            },
        };
        const prefixSelector = getFieldDecorator('prefix', {
            initialValue: '86',
        })(
            <Select className="icp-selector" style={{width: '60px'}}>
                <Option value="86">+86</Option>
            </Select>
        );
        return (
            <div className="gutter-example">
                <BreadcrumbCustom first="汰换" second="淘汰"/>

                <Form layout="inline" onSubmit={this.handleSubmit}>
                    <div className="gutter-box">
                        <Card title="水平表单" bordered={false}>
                            <FormItem
                                {...formItemLayout}
                                label="常住地址"
                            >
                                {getFieldDecorator('residence', {
                                    initialValue: ['zhejiang'],
                                    rules: [{type: 'array', required: true, message: '请选择你的常住地址!'}],
                                })(
                                    <Cascader options={this.state.residences}/>
                                )}

                            </FormItem>
                        </Card>
                    </div>
                </Form>

            </div>
        )
    }
}

const MapUi = Form.create()(BasicForms);

export default MapUi;
